import React from "react";
import {connect} from "react-redux";
import * as actions from "../redux/action-creator/actions";
class Home extends React.Component {
  constructor(props){
    super(props);
    this.state = {firstNameSort: true, lastNameSort: true, AgeSort: true, SexSort: true};
  }
  componentDidMount() {
    this.props.getList();
  }
  render() {
    return (
      <div>
        <table>
          <thead>
            <tr>
              <th onClick={() => this.props.sort("FirstName",this.state.firstNameSort)}>First name</th>
              <th>Last name</th>
              <th>Age</th>
              <th>Sex</th>
            </tr>
          </thead>
          <tbody>
            {this.props.userList.data.map((user,index) => {
              return (
                <tr key={user._id}>
                  <td>{user.firstName}</td>
                  <td>{user.lastName}</td>
                  <td>{user.Age}</td>
                  <td>{user.Sex}</td>
                  <td><button onClick={() => {this.props.getUser(user._id);}}/>edit</td>
                  <td><button onClick={() => {this.props.deleteOneUser(user._id)}}/>delete</td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <button onClick={() => {this.props.history.push(`/create`);}}/>
      </div>
    );
  }
}
const mapStatetoProps = (state) => {
  return {
    userList: state.userListReducer,
    user: state.userReducer
  }
}
const mapDispatchtoProps = (dispatch) => {
  return {
    getList: () => {
      dispatch(actions.getList());
    },
    getUser: (id) => {
      dispatch(actions.getOneUser(id));
    },
    deleteOneUser: (id) => {
      dispatch(actions.deleteOneUser(id));
    },
    createOneUser: (user) => {
      dispatch(actions.createOneUser(user));
    },
    updateOneUser: (id, user) => {
      dispatch(actions.updateOneUser(id, user));
    },
    sort: (field, AorD) => {
      dispatch(actions.sort(field, AorD));
    }
  }
}
export default connect(mapStatetoProps,mapDispatchtoProps)(Home);
