import { sortFirstName } from "../action-creator/actions";

const initState = {data:[], isLoading:false, err:""};
const userListReducer = (state = initState, action) => {
  switch(action.type){
    case "GET_LIST_START":
      return {
        ...state,
        isLoading: true
      }
    case "GET_LIST_SUCCESS":
      return {
        ...state,
        isLoading: false,
        data: action.data
      }
    case "GET_LIST_FAIL":
      return {
        ...state,
        isLoading: false,
        err: action.err
      }
    case "SORT_FIRSTNAME":
      return {
        ...state,
        data:sort("FirstName", state.data)
      }
    default:
      return state;
  }
}
sort = (type, data) => {
  switch(type){
    case "FirstName": 
      let newArr = [...data];
      newArr.sort((a, b) => a.firstName > b.firstName);
  }
}
export default userListReducer;
