import React from "react";
import {connect} from "react-redux";
import * as actions from "../redux/action-creator/actions";
class Create extends React.Component {
  constructor(props){
    super(props);
    this.state = {user:{firstName:"",lastName:"",Age:"",Sex:"",password:""},
      firstNamePass: false, lastNamePass: false, AgePass: false, SexPass: false, passwordPass: false,};
  }
  handleFirstName = (e) => {
    console.log(e.target.value.length);
    if(e.target.value.length === 0){
      this.setState({user:{...this.state.user,firstName: e.target.value},firstName:false})
    }else{
      this.setState({user:{...this.state.user,firstName: e.target.value},firstNamePass:true});
    }
  }
  handleLastName = (e) => {
    this.setState({user:{...this.state.user,lastName: e.target.value}});
  }
  handleAge = (e) => {
    this.setState({user:{...this.state.user,Age: e.target.value}});
  }
  handleSex = (e) => {
    console.log(e);
    this.setState({user:{...this.state.user,Sex: e.target.value}});
  }
  handlePassword = (e) => {
    this.setState({user:{...this.state.user,password: e.target.value}});
  }
  handleSubmit = e => {
    e.preventDefault();
    this.props.createOneUser(this.state.user);
  }
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div>First Name:{!this.state.firstNamePass&&<span>please input a valid firstname</span>}</div>
          <input type="text" value={this.state.user.firstName} onChange={this.handleFirstName}/>
          <div>Last Name:</div>
          <input type="text" value={this.state.user.lastName} onChange={this.handleLastName}/>
          <div>Age:</div>
          <input type="text" value={this.state.user.Age} onChange={this.handleAge}/>
          <div>Sex:</div>
          <select value={this.state.user.Sex} onChange={this.handleSex}>
          　<option value="Male">Male</option>
          　<option value="Female">Female</option>
          </select>
          <div>Password:</div>
          <input type="text" value={this.state.user.password} onChange={this.handlePassword}/>
          <input type="submit"/>
        </form>
      </div>
    );
  }
}
const mapStatetoProps = (state) => {
  return {
    userList: state.userListReducer,
    user: state.userReducer
  }
}
const mapDispatchtoProps = (dispatch) => {
  return {
    getList: () => {
      dispatch(actions.getList());
    },
    getUser: (id) => {
      dispatch(actions.getOneUser(id));
    },
    deleteOneUser: (id) => {
      dispatch(actions.deleteOneUser(id));
    },
    createOneUser: (user) => {
      dispatch(actions.createOneUser(user));
    },
    updateOneUser: (id, user) => {
      dispatch(actions.updateOneUser(id, user));
    }
  }
}
export default connect(mapStatetoProps,mapDispatchtoProps)(Create);
