const axios = require('axios');
export const userListRequestStart = () => {
  return {
    type: "GET_LIST_START"
  }
}
export const userListGetSuccess = (res) => {
  return {
    type: "GET_LIST_SUCCESS",
    data: res
  }
}
export const userListGetFail = (err) => {
  return {
    type: "GET_LIST_FAIL",
    err: err
  }
}
export const getList = () => {
  return (dispatch, getState) => {
    dispatch(userListRequestStart());
    axios
      .get('http://localhost:8888/api/users/getall')
      .then(res => {
        dispatch(userListGetSuccess(res.data))
      })
      .catch(error => {
        dispatch(userListGetFail(error));
      });
  }
}
//sigle user
export const userRequestStart = () => {
  return {
    type: "USER_START"
  }
}
export const userGetSuccess = (res) => {
  return {
    type: "GET_USER_SUCCESS",
    data: res
  }
}
export const userRequestFail = (err) => {
  return {
    type: "USER_FAIL",
    err: err
  }
}
export const getOneUser = (id) => {
  return (dispatch, getState) => {
    dispatch(userRequestStart());
    axios
      .get(`http://localhost:8888/api/users/getone/${id}`)
      .then(res => {
        dispatch(userGetSuccess(res.data))
      })
      .catch(error => {
        dispatch(userRequestFail(error));
      });
  }
}
export const deleteOneUser = (id) => {
  return (dispatch, getState) => {
    dispatch(userRequestStart());
    axios
      .delete(`http://localhost:8888/api/users/deleteone/${id}`)
      .then(res => {
        const newList = getState().userListReducer.data.filter(user => {
          return user.id!==id;
        });
        dispatch(userListGetSuccess(newList))
      })
      .catch(error => {
        dispatch(userRequestFail(error));
      });
  }
}
export const createOneUser = (user) => {
  return (dispatch, getState) => {
    dispatch(userRequestStart());
    axios
      .post(`http://localhost:8888/api/users/createone`, user)
      .then(res => {
        const newList = getState().userListReducer.data.push(user);
        dispatch(userListGetSuccess(newList))
      })
      .catch(error => {
        dispatch(userRequestFail(error));
      });
  }
}
