import React from "react";
import {connect} from "react-redux"
import * as actions from "../redux/action-creator/actions"
class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {user:{firstName:"",lastName:"",Age:"",sex:"",password:""}};
  }
  handleFirstName = (e) => {
    this.setState({user:{firstName: e.target.value}});
  }
  handleLastName = (e) => {
    this.setState({user:{lastName: e.target.value}});
  }
  handleAge = (e) => {
    this.setState({user:{Age: e.target.value}});
  }
  handleSex = (e) => {
    this.setState({user:{sex: e.target.value}});
  }
  handlePassword = (e) => {
    this.setState({user:{password: e.target.value}});
  }
  render() {
    console.log(this.props);
    return (
      <div>
        <button onClick={() => this.props.getList()}></button>
        <button onClick={() => this.props.deleteOneUser("5d9aaedb1c9d44000067ed42")}></button>
        <form>
          <input type="text" value={this.state.user.firstName} onChange={this.handleFirstName}/>
          <input type="text" value={this.state.user.lastName} onChange={this.handleLastName}/>
          <input type="text" value={this.state.user.Age} onChange={this.handleAge}/>
          <input type="text" value={this.state.user.sex} onChange={this.handleSex}/>
          <input type="text" value={this.state.user.password} onChange={this.handlePassword}/>
        </form>
      </div>
    );
  }
}
const mapStatetoProps = (state) => {
  return {
    userList: state.userListReducer,
    user: state.userReducer
  }
}
const mapDispatchtoProps = (dispatch) => {
  return {
    getList: () => {
      dispatch(actions.getList());
    },
    getUser: (id) => {
      dispatch(actions.getOneUser(id));
    },
    deleteOneUser: (id) => {
      dispatch(actions.deleteOneUser(id));
    },
    createOneUser: (user) => {
      dispatch(actions.createOneUser(user));
    }
  }
}
export default connect(mapStatetoProps,mapDispatchtoProps)(App);
