import React from "react";
import {connect} from "react-redux";
import * as actions from "../redux/action-creator/actions";
class Home extends React.Component {
  constructor(props){
    super(props);
    this.state = {firstNameSort: true, lastNameSort: true, AgeSort: true, SexSort: true, search:"",page: 1};
  }
  componentDidMount() {
    this.props.getListAndjump();
    this.setState({data:this.props.userList.data});
  }
  handleFirstnameClick = () => {
    this.props.sort("FirstName",this.state.firstNameSort);
    this.setState({firstNameSort: !this.state.firstNameSort});
  }
  handleLastnameClick = () => {
    this.props.sort("LastName",this.state.lastNameSort);
    this.setState({lastNameSort: !this.state.lastNameSort});
  }
  handleAgeClick = () => {
    this.props.sort("Age",this.state.AgeSort);
    this.setState({AgeSort: !this.state.AgeSort});
  }
  handleSexClick = () => {
    this.props.sort("Sex",this.state.SexSort);
    this.setState({SexSort: !this.state.SexSort});
  }
  handleSearch = (e) => {
    this.setState({search:e.target.value});
    this.props.search(e.target.value);
    if(e.target.value === ""){
      this.props.getList();
    }
  }
  render() {
    console.log(this.state);
    return (
      <div>
        <input type="text" onChange={this.handleSearch} value={this.state.search}/>
        <table>
          <thead>
            <tr>
              <th onClick={this.handleFirstnameClick}>First name</th>
              <th onClick={this.handleLastnameClick}>Last name</th>
              <th onClick={this.handleAgeClick}>Age</th>
              <th onClick={this.handleSexClick}>Sex</th>
            </tr>
          </thead>
          <tbody>
            {this.props.userList.data.map((user,index) => {
              return (
                <tr key={user._id}>
                  <td>{user.firstName}</td>
                  <td>{user.lastName}</td>
                  <td>{user.Age}</td>
                  <td>{user.Sex}</td>
                  <td><button onClick={() => {this.props.getUser(user._id);}}>edit</button></td>
                  <td><button onClick={() => {this.props.deleteOneUser(user._id)}}>delete</button></td>
                </tr>
              );
            })}
          </tbody>
        </table>
        <div>

        </div>
        <button onClick={() => {this.props.history.push(`/create`);}}>create user</button>
      </div>
    );
  }
}
const mapStatetoProps = (state) => {
  return {
    userList: state.userListReducer,
    user: state.userReducer
  }
}
const mapDispatchtoProps = (dispatch) => {
  return {
    getListAndjump: () => {
      dispatch(actions.getListAndjump());
    },
    getList: () => {
      dispatch(actions.getList());
    },
    getUser: (id) => {
      dispatch(actions.getOneUser(id));
    },
    deleteOneUser: (id) => {
      dispatch(actions.deleteOneUser(id));
    },
    createOneUser: (user) => {
      dispatch(actions.createOneUser(user));
    },
    updateOneUser: (id, user) => {
      dispatch(actions.updateOneUser(id, user));
    },
    sort: (field, AorD) => {
      dispatch(actions.sort(field, AorD));
    },
    search: (text) => {
      dispatch(actions.search(text));
    }
  }
}
export default connect(mapStatetoProps,mapDispatchtoProps)(Home);
