import React from "react";
import {connect} from "react-redux";
import * as actions from "../redux/action-creator/actions";
import { makeStyles } from '@material-ui/core/styles';
import FilledInput from '@material-ui/core/FilledInput';
import FormControl from '@material-ui/core/FormControl';
import FormHelperText from '@material-ui/core/FormHelperText';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import OutlinedInput from '@material-ui/core/OutlinedInput';

class Create extends React.Component {
  constructor(props){
    super(props);
    this.state = {user:{firstName:"",lastName:"",Age:"",Sex:" ",password:""},
      firstNamePass: false, lastNamePass: false, AgePass: false, SexPass: false, passwordPass: false};
  }
  handleFirstName = (e) => {
    if(e.target.value.length === 0){
      this.setState({user:{...this.state.user,firstName: e.target.value},firstNamePass:false})
    }else{
      this.setState({user:{...this.state.user,firstName: e.target.value},firstNamePass:true});
    }
  }
  handleLastName = (e) => {
    if(e.target.value.length === 0){
      this.setState({user:{...this.state.user,lastName: e.target.value},lastNamePass:false})
    }else{
      this.setState({user:{...this.state.user,lastName: e.target.value},lastNamePass:true});
    }
  }
  handleAge = (e) => {
    if(Number(e.target.value) > 0 && Number(e.target.value) <= 120){
      this.setState({user:{...this.state.user,Age: e.target.value},AgePass:true})
    }else{
      this.setState({user:{...this.state.user,Age: e.target.value},AgePass:false});
    }
  }
  handleSex = (e) => {
    console.log(e.target.value);
    if(e.target.value === "None"){
      this.setState({user:{...this.state.user,Sex: e.target.value},SexPass:false});
    }else{
      this.setState({user:{...this.state.user,Sex: e.target.value},SexPass:true});
    }

  }
  handlePassword = (e) => {
    if(e.target.value.length >= 8 && e.target.value.length <= 12){
      this.setState({user:{...this.state.user,password: e.target.value},passwordPass:true})
    }else{
      this.setState({user:{...this.state.user,password: e.target.value},passwordPass:false});
    }
  }
  handleSubmit = e => {
    e.preventDefault();
    this.props.createOneUser(this.state.user, "/home");
  }
  render() {
    const {firstNamePass, lastNamePass, AgePass, SexPass, passwordPass} = this.state;
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <FormControl>
            <InputLabel htmlFor="component-helper">Firstname</InputLabel>
            <Input
              id="component-helper"
              value={this.state.user.firstName}
              onChange={this.handleFirstName}
              aria-describedby="component-helper-text"
            />
            {!this.state.firstNamePass&&<FormHelperText id="component-error-text">please input a valid firstname</FormHelperText>}
          </FormControl>
          <FormControl>
            <InputLabel htmlFor="component-helper">Lastname</InputLabel>
            <Input
              id="component-helper"
              value={this.state.user.lastName}
              onChange={this.handleLastName}
              aria-describedby="component-helper-text"
            />
            {!this.state.lastNamePass&&<FormHelperText id="component-error-text">please input a valid lastname</FormHelperText>}
          </FormControl>
          <div>Age:{!this.state.AgePass&&<span>please input a valid age</span>}</div>
          <input type="text" value={this.state.user.Age} onChange={this.handleAge}/>
          <div>Sex:{!this.state.SexPass&&<span>please select a valid Sex</span>}</div>
          <select value={this.state.user.Sex} onChange={this.handleSex}>
            <option value="None">Please Select</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
          <div>Password:{!this.state.passwordPass&&<span>please enter a valid password between 8 to 12 character</span>}</div>
          <input type="text" value={this.state.user.password} onChange={this.handlePassword}/>
          <input type="submit" disabled={!(firstNamePass && lastNamePass && AgePass && SexPass && passwordPass)}/>
        </form>
      </div>
    );
  }
}
const mapStatetoProps = (state) => {
  return {
    userList: state.userListReducer,
    user: state.userReducer
  }
}
const mapDispatchtoProps = (dispatch) => {
  return {
    getList: () => {
      dispatch(actions.getList());
    },
    getUser: (id) => {
      dispatch(actions.getOneUser(id));
    },
    deleteOneUser: (id) => {
      dispatch(actions.deleteOneUser(id));
    },
    createOneUser: (user,url) => {
      dispatch(actions.createOneUser(user,url));
    },
    updateOneUser: (id, user) => {
      dispatch(actions.updateOneUser(id, user));
    }
  }
}
export default connect(mapStatetoProps,mapDispatchtoProps)(Create);
