import React from "react";
import {connect} from "react-redux";
import * as actions from "../redux/action-creator/actions";
class Edit extends React.Component{
  constructor(props){
    super(props);
    this.state = {user:{firstName:"",lastName:"",Age:"",Sex:"",password:""}};
  }
  componentDidMount() {
    const id = this.props.match.params.id;
    console.log(this.props.user.data);
  }
  handleFirstName = (e) => {
    this.setState({user:{...this.state.user,firstName: e.target.value}});
  }
  handleLastName = (e) => {
    this.setState({user:{...this.state.user,lastName: e.target.value}});
  }
  handleAge = (e) => {
    this.setState({user:{...this.state.user,Age: e.target.value}});
  }
  handleSex = (e) => {
    this.setState({user:{...this.state.user,Sex: e.target.value}});
  }
  handlePassword = (e) => {
    this.setState({user:{...this.state.user,password: e.target.value}});
  }
  handleSubmit = (e) => {
    e.preventDefault();
    this.props.updateOneUser(this.props.match.params.id, this.state.user);
  }
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input type="text" value={this.state.user.firstName} onChange={this.handleFirstName}/>
          <input type="text" value={this.state.user.lastName} onChange={this.handleLastName}/>
          <input type="text" value={this.state.user.Age} onChange={this.handleAge}/>
          <input type="text" value={this.state.user.Sex} onChange={this.handleSex}/>
          <input type="text" value={this.state.user.password} onChange={this.handlePassword}/>
          <input type="submit"/>
        </form>
      </div>
    );
  }
}
const mapStatetoProps = (state) => {
  return {
    userList: state.userListReducer,
    user: state.userReducer
  }
}
const mapDispatchtoProps = (dispatch) => {
  return {
    getList: () => {
      dispatch(actions.getList());
    },
    getUser: (id) => {
      dispatch(actions.getOneUser(id));
    },
    deleteOneUser: (id) => {
      dispatch(actions.deleteOneUser(id));
    },
    createOneUser: (user) => {
      dispatch(actions.createOneUser(user));
    },
    updateOneUser: (id, user) => {
      dispatch(actions.updateOneUser(id, user));
    }
  }
}
export default connect(mapStatetoProps,mapDispatchtoProps)(Edit);
