import React from 'react';
import {connect} from "react-redux";
import * as actions from "../redux/action-creator/actions";
import MaterialTable from 'material-table';

const axios = require('axios');
class NewHome extends React.Component {
  constructor(props){
    super(props);
    this.state = {columns: [
      { title: 'FirstName', field: 'firstName' },
      { title: 'LastName', field: 'lastName' },
      { title: 'Age', field: 'Age', type: 'numeric' },
      { title: 'Sex', field: 'Sex' },
    ],  data: this.props.userList.data}
  }
  componentDidMount() {
    this.props.getList();
    axios
      .get('http://localhost:8888/api/users/getall')
      .then(res => {
        this.setState({data: this.props.userList.data});
      })
  }
  render() {
    console.log(this.props);
    return (

      <MaterialTable
        title="Editable Example"
        columns={this.state.columns}
        data={this.state.data}
        editable={{
          onRowAdd: newData =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                this.setState(prevState => {
                  const data = [...prevState.data];
                  data.push(newData);
                  return { ...prevState, data };
                });
              }, 600);
            }),
          onRowUpdate: (newData, oldData) =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                if (oldData) {
                  this.setState(prevState => {
                    const data = [...prevState.data];
                    data[data.indexOf(oldData)] = newData;
                    return { ...prevState, data };
                  });
                }
              }, 600);
            }),
          onRowDelete: oldData =>
            new Promise(resolve => {
              setTimeout(() => {
                resolve();
                this.setState(prevState => {
                  const data = [...prevState.data];
                  data.splice(data.indexOf(oldData), 1);
                  return { ...prevState, data };
                });
              }, 600);
            }),
        }}
      />
    );
  }
}
const mapStatetoProps = (state) => {
  return {
    userList: state.userListReducer,
    user: state.userReducer
  }
}
const mapDispatchtoProps = (dispatch) => {
  return {
    getListAndjump: (url) => {
      dispatch(actions.getListAndjump(url));
    },
    getList: () => {
      dispatch(actions.getList());
    },
    getUser: (id) => {
      dispatch(actions.getOneUser(id));
    },
    deleteOneUser: (id) => {
      dispatch(actions.deleteOneUser(id));
    },
    createOneUser: (user) => {
      dispatch(actions.createOneUser(user));
    },
    updateOneUser: (id, user) => {
      dispatch(actions.updateOneUser(id, user));
    },
    sort: (field, AorD) => {
      dispatch(actions.sort(field, AorD));
    },
    search: (text) => {
      dispatch(actions.search(text));
    }
  }
}
export default connect(mapStatetoProps,mapDispatchtoProps)(NewHome);
